import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./css/LoginForm.css";

function LoginForm() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await axios.post("http://backend:8080/auth/login", {
      username,
      password,
    });


      const token = response.data.replace("Bearer ", "");
      localStorage.setItem("token", token);
      alert("GiriÅŸ baÅŸarÄ±lÄ±!");
    } catch (err) {
        console.error("GiriÅŸ hatasÄ±:", err);

        // ðŸ”½ Sunucudan gelen Ã¶zel hata mesajÄ±nÄ± gÃ¶ster
        if (err.response && err.response.data) {
            setError(err.response.data); // Ã¶rn: "Ã‡ok fazla hatalÄ± giriÅŸ yapÄ±ldÄ±..."
        } else {
            setError("Sunucu hatasÄ± oluÅŸtu.");
        }
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>JWT Login</h2>
        <input
          type="text"
          placeholder="KullanÄ±cÄ± AdÄ±"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Åžifre"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">GiriÅŸ Yap</button>
        {error && <p className="error">{error}</p>}

        <p className="register-link">
          HesabÄ±n yok mu? <Link to="/register">KayÄ±t Ol</Link>
        </p>
      </form>
    </div>
  );
}

export default LoginForm;
